import React, { useEffect, useState } from "react";
import SideBar from "./SideBar";
import Report from "./Report";
import TransactionTransaction from "./TransactionTransaction";
import History from "./History";
import Incomes from "./IncomesPage";


function DashBoard() {
    const [dash, setDash] = useState(true);
    const openDash = () => {
        setDash(true);
    };

    const closeDash = () => {
        setDash(false);
    };

    const [incomes, setIncomes] = useState(false);
    const openIncomes = () => {
        setIncomes(true);
    };

    const closeIncomes = () => {
        setIncomes(false);
    };
    const [Transaction, setTransaction] = useState(false);
    const openTransaction = () => {
        setTransaction(true);
    };

    const closeTransaction = () => {
        setTransaction(false);
    };
    const [history, setHistory] = useState(false);
    const openHistory = () => {
        setHistory(true);
    };

    const closeHistory = () => {
        setHistory(false);
    };
    return (
        <div className="grid grid-cols-8 box-border h-screen gap-4 p-5 bg-gradient-to-b from-slate-700 to-black">
            <SideBar 
            closeDash={closeDash} openDash={openDash}
            openTransaction={openTransaction} closeTransaction={closeTransaction}
            openHistory={openHistory} closeHistory={closeHistory}
            openIncomes={openIncomes} closeIncomes={closeIncomes}>
            </SideBar>
            {dash && <Report/>}
            {Transaction && <TransactionTransaction/>}
            {history && <History/>}
            {incomes && <Incomes/>}
        </div>
    )
}

export default DashBoard
