import React, { useEffect, useState } from "react";
import SideBar from "./SideBar";

function DashBoard() {
    const [dash, setDash] = useState(true);
    const openDash = () => {
        setDash(true);
    };

    const closeDash = () => {
        setDash(false);
    };

    useEffect(()=>{
        console.log('')
    },[dash])

    const [incomes, setIncomes] = useState(false);
    const openIncomes = () => {
        setIncomes(true);
    };

    const closeIncomes = () => {
        setIncomes(false);
    };
    const [add, setAdd] = useState(false);
    const openAdd = () => {
        setAdd(true);
    };

    const closeAdd = () => {
        setDash(false);
    };
    const [history, setHistory] = useState(false);
    const openHistory = () => {
        setHistory(true);
    };

    const closeHistory = () => {
        setHistory(false);
    };
    return (
        <div className="grid grid-cols-8 box-border h-screen gap-4 p-5 bg-gradient-to-b from-gray-500 to-gray-900">
            <SideBar 
            closeDash={closeDash} openDash={openDash}
            openAdd={openAdd} closeAdd={closeAdd}
            openHistory={openHistory} closeHistory={closeHistory}
            openIncomes={openIncomes} closeIncomes={closeIncomes}>
            </SideBar>
            {dash && <div className="col-span-6 box-border bg-white bg-opacity-75 rounded-md p-4">
                
                <h1>DashBoard</h1></div>}
            {add && <div className="col-span-6 bg-black bg-opacity-75 rounded-md">
                <h1>Add</h1></div>}
            {history && <div className="col-span-6 bg-purple-600 bg-opacity-75 rounded-md">
                <h1>history</h1></div>}
            {incomes && <div className="col-span-6 bg-yellow-500 bg-opacity-75 rounded-md">
                <h1>Incomes</h1></div>}
        </div>
    )
}

export default DashBoard
