import React from "react";
import Card from "./Card";

function Features(){
    return(
        <div className="text-center mb-4 min-h-[86vh]" id='features'>
                <h1 className="text-4xl py-10 font-semibold bg-gradient-to-r from-gray-400 to-gray-600">Empowering Features</h1>
                <div className="flex flex-wrap justify-evenly gap-4 p-6 text-black">
                    <Card heading={'About ExpenseTracker'} 
                        detail={'Effortlessly record and categorize your expenses for a clear financial overview.'}
                        image={'https://images.unsplash.com/photo-1709534486708-fb8f94150d0a?q=80&w=2001&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D'}/>
                    
                    <Card heading={'About ExpenseTracker'} 
                        detail={'Effortlessly record and categorize your expenses for a clear financial overview.'}
                        image={'https://images.unsplash.com/photo-1709534486708-fb8f94150d0a?q=80&w=2001&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D'}/>

<Card heading={'About ExpenseTracker'} 
                        detail={'Effortlessly record and categorize your expenses for a clear financial overview.'}
                        image={'https://images.unsplash.com/photo-1709534486708-fb8f94150d0a?q=80&w=2001&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D'}/>

<Card heading={'About ExpenseTracker'} 
                        detail={'Effortlessly record and categorize your expenses for a clear financial overview.'}
                        image={'https://images.unsplash.com/photo-1709534486708-fb8f94150d0a?q=80&w=2001&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D'}/>
                    <div className="flex flex-col gap-5 w-1/5 hover:-translate-y-1 p-2 rounded-xl hover:shadow-white shadow-lg bg-black text-white hover:text-purple-400 hover:ease-in-out duration-500">
                        <h2 className="pt-2 font-semibold px-2">Strategic Budget Planning</h2>
                        <p className="px-2">Create and manage budgets across various categories to optimize your spending..</p>
                    </div>
                    <div className="flex flex-col gap-5 w-1/5 hover:-translate-y-1 bg-black text-white p-2 rounded-xl hover:shadow-white shadow-lg hover:text-purple-400 hover:ease-in-out duration-500">
                        <h2 className="pt-2 px-2 font-semibold">Insightful Financial Analytics</h2>
                        <p className="px-2">Gain valuable insights through detailed visualizations of your financial trends.</p>
                    </div>
                    <div className="flex flex-col gap-5 w-1/5 hover:-translate-y-1 bg-black p-2 rounded-xl hover:shadow-white shadow-lg text-white hover:text-purple-400 hover:ease-in-out duration-500">
                        <h2 className="pt-2 px-2 font-semibold">Bank-Grade Security</h2>
                        <p className="px-2">Rest easy knowing your financial data is protected by state-of-the-art encryption.</p>
                    </div>
                </div>
            </div>
    )
}

export default Features
