import React, { useState } from "react";
import { FaUserCircle } from "react-icons/fa";


function SideBar({openDash, openHistory, openIncomes, openAdd, closeDash, closeAdd, closeHistory, closeIncomes}) {


    const handleAdd=()=>{
        closeDash();
        closeHistory();
        closeIncomes();
        openAdd();
        

    }
    const handleDash=()=>{
        closeAdd();
        closeHistory();
        closeIncomes();
        openDash();
    }
    const handleHistory=()=>{
        closeDash();
        closeAdd();
        closeIncomes();
        openHistory();
    }
    const handleIncomes=()=>{
        closeDash();
        closeHistory();
        closeAdd();
        openIncomes();
    }
    
    return (
        <>
            <div className="flex flex-col gap-4 box-border text-white font-semibold col-span-2 bg-gradient-to-b from-slate-500 to-slate-800 rounded-md bg-opacity-95 p-4">
                <a href=""><FaUserCircle className="text-8xl text-orange-400"/></a>
                <a href="#" className="hover:bg-slate-400 bg-opacity-10 rounded-md hover:ease-linear duration-150 p-1" onClick={handleDash} id="dash">DashBoard</a>
                <a href="#" className="hover:bg-slate-400 bg-opacity-10 rounded-md hover:ease-linear duration-150 p-1" onClick={handleAdd}>Add Transaction</a>
                <a href="#" className="hover:bg-slate-400 bg-opacity-10 rounded-md hover:ease-linear duration-150 p-1" onClick={handleHistory}>History</a>
                <a href="#" className="hover:bg-slate-400 bg-opacity-10 rounded-md hover:ease-linear duration-150 p-1" onClick={handleIncomes}>Incomes</a>
            </div>
        </>
    )
}

export default SideBar
