import React, { useEffect, useState } from "react";
import SideBar from "./SideBar";
import Report from "./Report";
import AddTransaction from "./AddTransaction";
import History from "./History";
import Incomes from "./IncomesPage";


function DashBoard() {
    const [dash, setDash] = useState(true);
    const openDash = () => {
        setDash(true);
    };

    const closeDash = () => {
        setDash(false);
    };

    const [incomes, setIncomes] = useState(false);
    const openIncomes = () => {
        setIncomes(true);
    };

    const closeIncomes = () => {
        setIncomes(false);
    };
    const [add, setAdd] = useState(false);
    const openAdd = () => {
        setAdd(true);
    };

    const closeAdd = () => {
        setAdd(false);
    };
    const [history, setHistory] = useState(false);
    const openHistory = () => {
        setHistory(true);
    };

    const closeHistory = () => {
        setHistory(false);
    };
    return (
        <div className="grid grid-cols-8 box-border h-screen gap-4 p-5 bg-gradient-to-b from-slate-800 to-black">
            <SideBar 
            closeDash={closeDash} openDash={openDash}
            openAdd={openAdd} closeAdd={closeAdd}
            openHistory={openHistory} closeHistory={closeHistory}
            openIncomes={openIncomes} closeIncomes={closeIncomes}>
            </SideBar>
            {dash && <Report/>}
            {add && <AddTransaction/>}
            {history && <History/>}
            {incomes && <Incomes/>}
        </div>
    )
}

export default DashBoard
