import React, {useState} from "react";
import { Link } from "react-router-dom";
import Login from "./Login";

function Header() {
    const [isModalOpen, setIsModalOpen] = useState(false);

    const openModal = () => {
      setIsModalOpen(true);
    };

    return (
        <div className={isModalOpen ? "background-overlay" : ""}>
        <div className='h-[14vh] w-full sticky top-0 bg-gradient-to-b from-slate-500 via-slate-800 to-black text-white flex justify-between items-center px-6 z-50'>
            <Link to="/"><h1 className="text-4xl">Expense Tracker</h1></Link>
            <div className='flex gap-5 items-center font-semibold'>
                <span className="hover:text-orange-500 hover:scale-125 hover:underline hover:ease-linear duration-300 underline-offset-4">
                    <Link to="/">Home</Link>
                </span>
                <span className="hover:ease-linear duration-300 hover:text-orange-500 hover:scale-125 hover:underline underline-offset-4">
                    <a href="#features">Features</a>
                </span>
                <span className="hover:ease-linear duration-300 hover:text-orange-500 hover:scale-125 hover:underline underline-offset-4">
                    <a href="#about">About</a>
                </span>
                <span className="hover:ease-linear duration-300 hover:text-orange-500 hover:scale-125 hover:underline underline-offset-4">
                    <a href="#contact">Contact</a>
                </span>
                <span onClick={openModal} className="hover:ease-linear duration-300 hover:text-orange-500 hover:scale-125 hover:underline underline-offset-4">
                    Login
                </span>
                {isModalOpen && <Login/>}
            </div>
        </div>
        </div>
    )
}

export default Header
