import React from 'react';

function Login(){
    return(
        <div className='w-screen h-screen bg-sky-200 flex flex-col items-center gap-10'>
            <h1 className='text-4xl text-orange-400 pt-4'>Login</h1>
            <form className='flex flex-col gap-5 bg-white bg-opacity-75 rounded-md 
            shadow-lg w-[50vw] p-10'>
                <div className='flex flex-col gap-5'>
                <label htmlFor="mail">email or username:</label>
                <input type='email' className='rounded-md' required name='mail'/>
                </div>
                <div className='flex flex-col gap-5'>
                <label htmlFor="password">email or username:</label>
                <input type='password' className='rounded-md' required name='password'/>
                </div>
                <button className="text-lg items bg-orange-400 rounded-md w-[15vw] py-1 align-se" 
                type='submit'>Login</button>
            </form>
        </div>
    );
}

export default Login;
