import React, { useState } from "react";
import { FaUserCircle } from "react-icons/fa";


function SideBar({openDash, openHistory, openIncomes, openAdd, closeDash, closeAdd, closeHistory, close}) {
    
    return (
        <>
            <div className="flex flex-col gap-4 box-border text-white font-semibold col-span-2 bg-gradient-to-b from-slate-500 to-slate-800 rounded-md bg-opacity-95 p-4">
                <a href=""><FaUserCircle className="text-8xl text-orange-400"/></a>
                <a href="#" className="hover:scale-125 hover:translate-x-7  hover:ease-linear duration-150" onClick={openDash}>DashBoard</a>
                <a href="#" className="hover:scale-125 hover:translate-x-7 hover:ease-linear duration-150" onClick={closeDash}>Add Transaction</a>
                <a href="#" className="hover:scale-125 hover:translate-x-7 hover:ease-linear duration-150">History</a>
                <a href="#" className="hover:scale-125 hover:translate-x-7 hover:ease-linear duration-150">Incomes</a>
            </div>
        </>
    )
}

export default SideBar
