import React from "react";

function Card({heading, detail, image}) {
    return (
        <>
            <div className="flex flex-col h-[50vh] gap-5 w-1/5 hover:-translate-y-1 hover:shadow-purple-500 shadow-lg bg-black text-white rounded-xl hover:text-purple-400 hover:ease-in-out duration-500">
                <h2 className="px-2 pt-2  font-semibold">{heading}</h2>
                <p className="px-2">{detail}</p>
                <img src={image} className="w-full h-"></img>
            </div>
        </>
    )
}

export default Card
